import React, { Component } from 'react'

//Conditonal Rendering using if/else, variable
class ConRendering extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             isLogedIn: false
        }
    }
    
    render() {

        /* //using if/else
        if(this.state.isLogedIn){
            return <div>Welcome Ak</div> //user logedIn
        } else{
            return <div>Welcome Guest</div> //user not logedIn
        } */

        //using variable
        let message           //declare variable
        if(this.state.isLogedIn){
            message = <div>Welcome Ak</div>
        } else {
            message = <div>Welcome Guest</div>
        }
        
        return message
    }
}

export default ConRendering
