import React, { Component } from 'react'
import LifecycleChild from './LifecycleChild'

export class LifecycleParent extends Component {

    //1)first costructor excecute for Mounting Method
    constructor(props) {
        super(props)
    
        this.state = {
             name:'Ak',
        }
        console.log('MMP Constuctor')
    }

    //2)second this method excecute for Mounting Method
    //1)first this method execute for Updating Method
    static getDerivedStateFromProps(props, state) {
        console.log('MMP getDerivedStateFromProps')
        return null
    }    

    //2)second excecute this method for Upadating Method
    shouldComponentUpdate() {
        console.log('UMP shouldComponentUpdate')
        return true
    }

    //4)fourth excecute this method for Upadating Method
    getSnapshotBeforeUpdate() {
        console.log('UMP getSnapshotBeforeUpdate')
        return null
    }

    //5)fifth excecute this method for Upadating Method
    componentDidUpdate(){
        console.log('UMP componentDidUpdate')
    }

    //4)fourth this method excecute for Mounting Method
    componentDidMount() {
        console.log('MMP componentDidMount')
    }

    //method for changing state
    changeState = () => {
        this.setState({
            name: 'Aj'
        })
    }

    //3)third render method excecute for Mounting Method
    //3)third render method excecute for Updating Method
    render() {
        console.log('MMP Render')
        return (
        <div>
            <div>Parent Lifecycle</div>
            {/* this is call for child component so all 4 method of child compnent execute and after parent's 4th method execute */}
            <LifecycleChild></LifecycleChild> 
            <button onClick={this.changeState}>Change State</button>
        </div>
            
        )
    }
}

export default LifecycleParent
