import React, { Component } from 'react'

class SetState extends Component {

    constructor(){
        super()
        // intializing state
        this.state = {
            messege : 'Hello Visitor',
            count : 0
        }
    }

    // passing object to setState
    changeMessege() {
        this.setState({
            messege : 'Thank you for subscribing'
        })
    }

    // passing aero function to setState using previous state(proper way)
    increment(){
        this.setState((prev) => ({
            count : prev.count + 1
        }))
    }

    render() {
        return (
            <div> 
                <h1>{this.state.messege}</h1>
                {/* changing text on button click */}
                <button onClick={() => this.changeMessege()}>Subcribe</button>

                <h1>Count - {this.state.count}</h1>
                {/* increment count value on button click */}
                <button onClick={() => this.increment()}>Increment</button>
            </div>
        )
    }
}

export default SetState
