import React, { Component } from 'react'
import RegComp from './RegComp'
import PureComp from './PureComp'

export class ParentComp extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             name:'Ak'
        }
    }

    componentDidMount(){
        setInterval(() => {
            this.setState({
                name:'Ak'
            })
        }, 2000);
    }
    
    render() {
        console.log('-----Parent Comp-----')
        return (
            <div>
                Parent Component
                <RegComp></RegComp>
                <PureComp></PureComp>
            </div>
        )
    }
}

export default ParentComp
