import React, { Component } from 'react'

class FormBasic extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             name:'',
             comments:'',
             tech:''
        }
    }

    nameChangeHandler = event => {
        this.setState({
            name: event.target.value
        })
    }

    commentChangeHandler = event => {
        this.setState({
            comments:event.target.value
        })
    }

    techChangeHandler = event => {
        this.setState({
            tech: event.target.value,
        })
    }
    
    render() {
        return (
            <form>
                <div>
                    <label>Name : </label>
                    <input type='text' value={this.state.name} onChange={this.nameChangeHandler}></input>
                </div>
                <div>
                    <label>Comments : </label>
                    <textarea value={this.state.comments} onChange={this.commentChangeHandler}></textarea>
                </div>
                <div>
                    <label>Tech : </label>
                    <select value={this.state.tech} onChange={this.techChangeHandler}>
                        <option value='react'>React</option>
                        <option value='angular'>Angular</option>
                        <option value='vue'>Vue</option>
                    </select>
                </div>
            </form>
        )
    }
}

export default FormBasic
