import React, { Component } from 'react'
import LifecycleChild from './LifecycleChild'

export class LifecycleParent extends Component {

    //1)first costructor excecute
    constructor(props) {
        super(props)
    
        this.state = {
             name:'Ak',
        }
        console.log('MMP Constuctor')
    }

    //2)second this method excecute
    static getDerivedStateFromProps(props, state) {
        console.log('MMP getDerivedStateFromProps')
        return null
    }    

    //4)fourth this method excecute
    componentDidMount() {
        console.log('MMP componentDidMount')
    }

    //3)third render method excecute
    render() {
        console.log('MMP Render')
        return (
        <div>
            {/* this is call for child component so all 4 method of child compnent execute and after parent's 4th method execute */}
            <LifecycleChild></LifecycleChild> 
            <div>Parent Lifecycle</div>
        </div>
            
        )
    }
}

export default MountingMethodsParent
